# Encoding: UTF-8

from browser import document, html

def upload_button(form_div_id):
  form_div = document.querySelector(form_div_id)

  if form_div:
    form_div.querySelector('input[type="submit"]').hidden = True
    upload = form_div.querySelector('input[type="file"]')
    upload.onchange = form_div.querySelector('form').submit
    upload.hidden = True

    button = html.BUTTON('Upload')
    button.onclick = upload.click

    form_div.appendChild(button)

